function setCookie(name, value, days) {
  var now = new Date()
    , expireDate= new Date();

  expireDate.setTime(now.getTime() + days*24*60*60*1000); 
  var curCookie = name + "=" + escape(value) + ";path=/;domain=.designedforresult.com;expires=" + expireDate; 
  document.cookie = curCookie;
}


function getCookie(name) {
  var dc = document.cookie;
  var prefix = name + "=";
  var begin = dc.indexOf("; " + prefix);
  if (begin == -1) {
    begin = dc.indexOf(prefix);
    if (begin != 0) return null;
  } else
    begin += 2;
  var end = document.cookie.indexOf(";", begin);
  if (end == -1)
    end = dc.length;
  return unescape(dc.substring(begin + prefix.length, end));
}


function getRandomInt(min, max) {
  return Math.floor(Math.random() * (max - min)) + min;
};  

function passwordValidation(input) {

  var password = input.val()
    , validator = $('.password-validator')
    , error = false;

  if (/[a-z]/.test(password)) {
    validator.find('.lowercase').addClass('valid');     
  } else {
    error = true;
    validator.find('.lowercase').removeClass('valid');
  };

  if (/[0-9]/.test(password)) {
    validator.find('.number').addClass('valid');     
  } else {
    error = true;
    validator.find('.number').removeClass('valid');
  };

  if (/[A-Z]/.test(password)) {
    validator.find('.uppercase').addClass('valid');     
  } else {
    error = true;
    validator.find('.uppercase').removeClass('valid');
  };      

  if (/^.{6,}/.test(password)) {
    validator.find('.length').addClass('valid');     
  } else {
    error = true;
    validator.find('.length').removeClass('valid');
  }

  if (error) {
    input.parent().addClass('has-errors')
  } else {
    input.parent().removeClass('has-errors')
  }
};


function invalidForm(form) {
  var emailReg = /^([\w-\.]+@([\w-]+\.)+[\w-]{2,4})?$/
    , phoneReg = /^\(?([0-9]{3})\)?[-. ]?([0-9]{3})[-. ]?([0-9]{4})$/
    , usernameReg = /^\w+$/
    , passwordReg = /(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{6,}/
    , errors = {};

  form.find('.require-field').each(function(){
    var input = $(this)
      , val = $.trim(input.val());

    if(!val) {
      input.parent().addClass('has-errors');
      errors['required'] = 'Please fill in all required fields.'
    }

    if(input.hasClass('email') && !emailReg.test(val)) {
      input.parent().addClass('has-errors');
      errors['email'] = 'Invalid Email';
    } 

    if (input.hasClass('password') && !passwordReg.test(val)) {
      input.parent().addClass('has-errors');
      errors['password'] = "Invalid Password";
    }    

    if (input.hasClass('confirm') && val != $('input.password').val()) {
      input.parent().addClass('has-errors');
      errors['confirm'] = "Password does not match";
    }
 
    if(input.hasClass('phone') && !phoneReg.test(val) ) {
      input.parent().addClass('has-errors');
      errors['phone'] = "Invalid Phone Number";
    }
  });

  if ($.isEmptyObject(errors)) {
    return false;
  } else {
    reloadErrors(errors);
    return true;

  }
};

function reloadErrors(errors) {
  $.each(errors, function(k, v) {

    $('.notify-box.error').append('<li><i class="fa fa-exclamation-circle"></i> ' + v + '</li>');
  });
};